import { useState, useEffect } from 'react';
import './App.css';
import SignUp from './components/SignUp';
import Category from './components/Category';
import NewTransaction from './components/Transaction/NewTransaction';
import Transactions from './components/Transaction/Transactions';


const sample_transactions = [
  {
    id: '1',
    title: 'Toilet Paper',
    type: 'Expense',
    description: '250 rolls pack',
    amount: 24.12,
    date: new Date(2022, 2, 14),
  },
  { 
      id: '2', 
      title: 'New TV', 
      type: 'Expense',
      description: 'Sony 4K Ultra HD',
      amount: 799.49, 
      date: new Date(2022, 2, 28) },
  {
    id: '3',
    title: 'Car Insurance',
    type: 'Expense',
    description: 'yearly payment',
    amount: 294.67,
    date: new Date(2022, 4, 1),
  },
  {
    id: '4',
    title: 'New Chair',
    type: 'Expense',
    description: 'good guy special sale',
    amount: 450.00,
    date: new Date(2022, 5, 1),
  },
  {
    id: '5',
    title: 'Salary',
    type: 'Income',
    description: 'weekly salary',
    amount: 4000.00,
    date: new Date(2022, 1, 30),
  },
];

function App() {
  const [user, setUser] = useState({});
  const [transactions, setTransactions] = useState(sample_transactions);

  function signUp (user) {
    fetch('http://localhost:3000/users', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        user: {
          email: user.email,
          first_name: user.first_name,
          last_name: user.last_name,
          password: user.password,
          password_confirmation: user.password_confirmation
        }
      })
    })
    .then(response => response.json())
    .then(returnedUser => setUser(returnedUser))
  }

  const addTransactionHandler = (transaction) => {
    setTransactions((prevTransactions) => {
      return [transaction, ...prevTransactions];
    });
  }

  return (
    <div className="App">


      {user.email ? <h2>Welcome {useState.user.first_name}</h2> : 
        <SignUp signUp={signUp} />
      }
      <Category />
      <NewTransaction onAddTransaction={addTransactionHandler}/>
      <Transactions items={transactions}/>
    </div>
);
}
export default App;
