import React, { useState } from 'react';
import TransactionList from './TransactionList';
import TransactionsFilter from './TransactionsFilter';
import Card from '../Card'


const Transactions = (props) => {
    const current = new Date();
    const currentMonth = current.getMonth();
    const [filteredMonth, setFilteredMonth] = useState('');

    const filterChangeHandler = (selectedData) => {
        console.log(selectedData);
        setFilteredYear(selectedData.year);
        setFilteredMonth(selectedData.month);
    };

    const filteredTransactions = props.items.filter((transaction) => {
        if (filteredMonth === ""){
            return transaction.date.getFullYear().toString() === filteredYear;
        }else{
            return transaction.date.getFullYear().toString()=== filteredYear && transaction.date.getMonth().toString===filteredMonth;
        }
    });

    return (
      <div>
        <Card className='Transactions'>
          <TransactionsFilter
            selected={filteredYear}
            onChangeFilter={filterChangeHandler}
          />

          <TransactionList items={filteredTransactions} />
        </Card>
      </div>
    );
  };

  export default Transactions;
