import React, { useState } from 'react';


const TransactionsFilter = (props) => {
    const [selectedYear, setSelectedYear] = useState('2022');
    const [selectedMonth, setSelectedMonth] = useState('');

    const dropdownYearChangeHandler = (event) => {
      setSelectedYear(event.target.value);
    };
    const dropdownMonthChangeHandler = (event) => {
      setSelectedMonth(event.target.value);
    };
    const filterSubmitHandler = (event) => {
      event.preventDefault();

      const filteredData = {
        year: selectedYear,
        month: selectedMonth,
      };

      props.onChangeFilter(filteredData);

      console.log(filteredData);
    }
    return (
      <div className='transactions-filter'>
        <div className='transactions-filter__control'>
          <label> Filter by Month</label>
          <select value={props.selected} onChange={dropdownMonthChangeHandler}>
            <option value="">-</option>
            <option value='0'>Jan</option>
            <option value='1'>Feb</option>
            <option value='2'>Mar</option>
            <option value='3'>Apr</option>
            <option value='4'>May</option>
            <option value='5'>Jun</option>
            <option value='6'>Jul</option>
            <option value='7'>Aug</option>
            <option value='8'>Sep</option>
            <option value='9'>Oct</option>
            <option value='10'>Nov</option>
            <option value='11'>Dec</option>
          </select>
        </div>
      </div>
    );
  };

  export default TransactionsFilter;
