import React, { useState } from 'react';
import TransactionList from './TransactionList';
import TransactionsFilter from './TransactionsFilter';
import Card from '../Card'


const Transactions = (props) => {
    const [filteredYear, setFilteredYear] = useState('2022');


    const filterYearChangeHandler = (selectedYear) => {
      setFilteredYear(selectedYear);
    };
    const filterMonthChangeHandler = (selectedMonth) => {
        setFilteredMonth(selectedMonth);
      };

    const filteredTransactions = props.items.filter((transaction) => {
      return transaction.date.getFullYear().toString() === filteredYear;
    });

    return (
      <div>
        <Card className='Transactions'>
          <TransactionsFilter
            selected={filteredYear}
            onChangeFilter={filterYearChangeHandler}
          />
            <TransactionsFilter
            selected={filteredMonth}
            onChangeFilter={filterMonthChangeHandler}
          />
          <TransactionList items={filteredTransactions} />
        </Card>
      </div>
    );
  };

  export default Transactions;
