import React,{useState} from 'react';
import { Card , Row, Col } from 'react-bootstrap';
const IncomeExpense = (props) => {
    console.log(props);
    const [incomeAmount, setIncomeAmount] = useState(0.00);
    const [expenseAmount, setExpenseAmount] = useState(0.00);
    const balance = incomeAmount - expenseAmount;
    return(
        <Row>
            <Col md={1}>

            </Col>
            <Col md={2}>
                <Card bg='primary' text='white' className='mb-2'>
                <Card.Header>Income</Card.Header>
                <Card.Body>
                    <Card.Text>
                        {incomeAmount}
                    </Card.Text>
                </Card.Body>
                </Card>
            </Col> 
            <Col md={2}>
                <Card bg='secondary' text='white' className='mb-2'>
                <Card.Header>Expense</Card.Header>
                <Card.Body>
                    <Card.Text>
                        {expenseAmount}
                    </Card.Text>
                </Card.Body>
                </Card>
            </Col> 
            <Col md={2}>
                <Card bg='danger' text='white' className='mb-2'>
                <Card.Header>balance</Card.Header>
                <Card.Body>
                    <Card.Text>
                        {balance}
                    </Card.Text>
                </Card.Body>
                </Card>
            </Col> 
        </Row>
    )
}

export default IncomeExpense;
